/*
 * This is the primary high-level API for the project. In this folder there should be:
 * A class called InsightFacade, this should be in a file called InsightFacade.ts.
 * You should not change this interface at all or the test suite will not work.
 */

export enum InsightDatasetKind {
	Sections = "sections",
	Rooms = "rooms",
}

export interface InsightDataset {
	id: string;
	kind: InsightDatasetKind;
	numRows: number;
}

export interface InsightResult {
	[key: string]: string | number;
}

export class InsightError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, InsightError);
	}
}

export class NotFoundError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, NotFoundError);
	}
}

export class ResultTooLargeError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, ResultTooLargeError);
	}
}

export interface IInsightFacade {
	/**
	 * Add a dataset to insightUBC.
	 *
	 * @param id  The id of the dataset being added. Follows the format /^[^_]+$/
	 * @param content  The base64 content of the dataset. This content should be in the form of a serialized zip file.
	 * @param kind  The kind of the dataset
	 *
	 * @return Promise <string[]>
	 *
	 * The promise should fulfill on a successful add, reject for any failures.
	 * The promise should fulfill with a string array,
	 * containing the ids of all currently added datasets upon a successful add.
	 * The promise should reject with an InsightError describing the error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 * If id is the same as the id of an already added dataset, the dataset should be rejected and not saved.
	 *
	 * After receiving the dataset, it should be processed into a data structure of
	 * your design. The processed data structure should be persisted to disk; your
	 * system should be able to load this persisted value into memory for answering
	 * queries.
	 *
	 * Ultimately, a dataset must be added or loaded from disk before queries can
	 * be successfully answered.
	 */
	addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]>;

	/**
	 * Remove a dataset from insightUBC.
	 *
	 * @param id  The id of the dataset to remove. Follows the format /^[^_]+$/
	 *
	 * @return Promise <string>
	 *
	 * The promise should fulfill upon a successful removal, reject on any error.
	 * Attempting to remove a dataset that hasn't been added yet counts as an error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 *
	 * The promise should fulfill the id of the dataset that was removed.
	 * The promise should reject with a NotFoundError (if a valid id was not yet added)
	 * or an InsightError (invalid id or any other source of failure) describing the error.
	 *
	 * This will delete both disk and memory caches for the dataset for the id meaning
	 * that subsequent queries for that id should fail unless a new addDataset happens first.
	 */
	removeDataset(id: string): Promise<string>;

	/**
	 * Perform a query on insightUBC.
	 *
	 * @param query  The query to be performed.
	 *
	 * If a query is incorrectly formatted, references a dataset not added (in memory or on disk),
	 * or references multiple datasets, it should be rejected.
	 *
	 * @return Promise <InsightResult[]>
	 *
	 * The promise should fulfill with an array of results.
	 * The promise should reject with a ResultTooLargeError (if the query returns too many results)
	 * or an InsightError (for any other source of failure) describing the error.
	 */
	performQuery(query: unknown): Promise<InsightResult[]>;

	/**
	 * List all currently added datasets, their types, and number of rows.
	 *
	 * @return Promise <InsightDataset[]>
	 * The promise should fulfill an array of currently added InsightDatasets, and will only fulfill.
	 */
	listDatasets(): Promise<InsightDataset[]>;
}

import {
	IInsightFacade,
	InsightDataset,
	InsightDatasetKind,
	InsightResult,
	InsightError,
	NotFoundError,
} from "./IInsightFacade";

import {
	getData,
	getAnyKeys,
	selectKeyValuesInColumn,
	validateResultSize,
	filterByAnyKeys,
	filterByWhere,
	transform,
	getGroupKeys,
} from "./PerformQueryHelperFunctions";

import {getApplyKeys, getQueryAsJson, validateQuery} from "./ValidateQueryHelperFunctions";

import {
	parseBuildingTable,
	getChildNodeByNodeName,
	getContentAsBase64,
	getCurrentDatasets,
	validateId,
	validateSectionsFiles,
	validateRoomsFiles,
	getContentsOfFiles,
	getGeoLocation,
	mergeArrays,
	filterSectionFileNames,
	filterRoomsFileNames,
} from "./AddDatasetHelperFunctions1";

import * as fs from "fs-extra";
import * as parse5 from "parse5";
import {
	addRoomId,
	filterCacheData,
	getContentsRoomFiles,
	makeInsightResult,
	matchByMarker,
} from "./AddDatasetHelperFunctions2";
import {sortByString, sortByStrings} from "./HelperFunctions";

/**
 * This is the main programmatic entry point for the project.
 * Method documentation is in IInsightFacade
 *
 */
export default class InsightFacade implements IInsightFacade {
	constructor() {
		console.log("InsightFacadeImpl::init()");
	}

	public async addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]> {
		await validateId(id);
		let zip = await getContentAsBase64(content);
		let fileInFolder = zip.files;
		let fileNames = Object.keys(fileInFolder);
		// TODO didn't validate folder before
		// console.log("What is this: " + fileInFolder["courses/"].name);
		if (kind === "sections") {
			validateSectionsFiles(fileNames);
			let filteredFileNames = filterSectionFileNames(fileNames);
			let contentsInZip = await getContentsOfFiles(filteredFileNames, zip, id);
			let cacheData: object = makeInsightResult(id, kind, contentsInZip);
			await fs.ensureDir("./data");
			await fs.writeJson(`./data/${id}`, cacheData);
		}
		if (kind === "rooms") {
			validateRoomsFiles(fileNames);
			let file = zip.file("index.htm");
			if (file != null) {
				let jsonContent = await file.async("string");
				let jsonObject = parse5.parse(jsonContent); // TODO parse can throw error?
				let table = getChildNodeByNodeName(jsonObject, "tbody"); // TODO MAKE FETCH TABLES
				if (table.length === 0) {
					throw new InsightError("No matching table.");
				}
				let result = parseBuildingTable(table);
				let geoLocations: any[] = await getGeoLocation(result);
				mergeArrays(result, geoLocations); // TODO Result now contains everything from index!
				let filteredFileNames = filterRoomsFileNames(fileNames);
				let contentsInZip = await getContentsRoomFiles(filteredFileNames, zip, id);
				let unfilteredCacheData = matchByMarker(contentsInZip, result);
				let unLabeledCacheData = filterCacheData(unfilteredCacheData);
				let labeledCacheData = addRoomId(unLabeledCacheData, id);
				let cacheData: InsightResult = makeInsightResult(id, kind, labeledCacheData);
				await fs.ensureDir("./data");
				await fs.writeJson(`./data/${id}`, cacheData);
			}
		}
		let addedDatasets = await getCurrentDatasets();
		return Promise.resolve(addedDatasets);
	}

	public async performQuery(query: any): Promise<InsightResult[]> {
		let parsedQuery = getQueryAsJson(query);
		let parsedQueryKeys = Object.keys(parsedQuery);
		await validateQuery(query); // TODO add validations T-T
		let anyKeys: string[] = getAnyKeys(parsedQuery);
		if (parsedQueryKeys.includes("TRANSFORMATIONS")) {
			let applyObjects = query.TRANSFORMATIONS["APPLY"];
			for (let applyObject of applyObjects) {
				let applyObjectKeys = Object.keys(applyObject);
				let applyObjectKey = applyObjectKeys[0];
				let applyObjectValue = applyObject[applyObjectKey];
				let applyObjectValueKeys = Object.keys(applyObjectValue);
				anyKeys.push(applyObjectValue[applyObjectValueKeys[0]]);
			}
		}
		let allData: object[] = await getData();
		let relevantData = filterByAnyKeys(anyKeys, allData); // TODO if relevantData is empty it means there is no matching dataset!
		let queryResults = filterByWhere(parsedQuery.WHERE, relevantData);
		if (parsedQueryKeys.includes("TRANSFORMATIONS")) {
			queryResults = transform(parsedQuery.TRANSFORMATIONS, queryResults);
		}
		let order = query.OPTIONS.ORDER;
		if (typeof order === "string") {
			sortByString(order, queryResults);
		}
		if (typeof order === "object") {
			sortByStrings(order, queryResults);
		}
		selectKeyValuesInColumn(queryResults, anyKeys);
		validateResultSize(queryResults);
		return Promise.resolve(queryResults);
	}

	public async removeDataset(id: string): Promise<string> {
		// Check for invalid id
		if (id === "" || id.includes("_") || id.trim() === "") {
			return Promise.reject(new InsightError("Invalid id"));
		}

		try {
			await fs.ensureDir("./data");
			let fileNames = await fs.readdir("./data");
			// await fs.remove(`./data/${id}.json`);
			if (fileNames.includes(`${id}`)) {
				await fs.remove(`./data/${id}`);
			} else {
				return Promise.reject(new NotFoundError());
			}
			// Return the id of the dataset that was removed
			return Promise.resolve(id);
			// return id;
		} catch (err) {
			return Promise.reject(new InsightError("Error removing dataset: " + err));
		}
	}

	public async listDatasets(): Promise<InsightDataset[]> {
		try {
			// Check if the './data' directory exists. If not, return an empty array
			await fs.ensureDir("./data");
			let fileNames = await fs.readdir("./data");
			let filePromises = fileNames.map(async (fileName) => {
				try {
					let fileContent = await fs.readJson(`./data/${fileName}`);
					let jsonString = JSON.stringify(fileContent);
					let data = JSON.parse(jsonString);
					return {
						id: data.id,
						kind: data.kind,
						numRows: data.numRows,
					};
				} catch (err) {
					throw new InsightError("Corrupted persisted file");
				}
			});

			return await Promise.all(filePromises);
		} catch (err) {
			throw new InsightError("Error listing datasets: " + err);
		}
	}
}

import {InsightDatasetKind, InsightError, InsightResult, ResultTooLargeError} from "./IInsightFacade";
import JSZip = require("jszip");
import * as fs from "fs-extra";
import Decimal from "decimal.js";

export function filterByAnyKeys(columns: string[], persistedData: any[]) {
	return persistedData.filter((persistedDataItem) => {
		return dataContainsColumnKey(columns, persistedDataItem);
	});
}

function dataContainsColumnKey(anyKeys: string[], persistedDataItem: any): boolean {
	for (let anyKey of anyKeys) {
		if (Object.keys(persistedDataItem).includes(anyKey)) {
			return true;
		}
	}
	return false;
}

export function filterByWhere(query: any, relevantData: any[]) {
	return relevantData.filter((data) => {
		return performWhere(query, data);
	});
}

export function transform(query: any, data: any[]) {
	let groupKeys: string[] = query.GROUP; // Cohesion-coupling??
	// console.log(groupKeys);
	// console.log(data.length);
	let groups = makeGroups(groupKeys, data);
	let applyObjects: any[] = query.APPLY;
	let newQueryResults = [];
	// console.log(groups.length);
	for (let group of groups) {
		let queryResult = trimByKeys(groupKeys, group[0]);
		// console.log(queryResult);
		let appliedQueryResults = applyTransformation(applyObjects, group);
		for (let appliedQueryResult of appliedQueryResults) {
			queryResult = {...queryResult, ...appliedQueryResult};
		}
		newQueryResults.push(queryResult);
	}
	// console.log("query results", newQueryResults)
	return newQueryResults;
}

export function trimByKeys(keys: string[], queryResult: any) {
	let trimedQueryResult = {...queryResult};
	let qrKeys = Object.keys(trimedQueryResult);
	for (let key of qrKeys) {
		if (!keys.includes(key)) {
			delete trimedQueryResult[key];
		}
	}
	return trimedQueryResult;
}

export function applyTransformation(applyObjects: any[], group: any[]) {
	let result = [];
	for (let applyObject of applyObjects) {
		let key = Object.keys(applyObject)[0];
		let applyToken = Object.keys(applyObject[key])[0];
		let smkey = applyObject[key][applyToken];
		let applyResult = applyRule(applyToken, smkey, group);
		result.push({[key]: applyResult});
	}
	return result;
}

export function applyRule(applyToken: string, smKey: string, group: any[]) {
	switch (applyToken) {
		case "SUM":
			return applyRuleSum(smKey, group);
		case "MAX":
			return applyRuleMax(smKey, group);
		case "MIN":
			return applyRuleMin(smKey, group);
		case "COUNT":
			return applyRuleCount(smKey, group);
		case "AVG":
			return applyRuleAvg(smKey, group);
		default:
			throw new InsightError("ApplyToken is invalid");
	}
}

export function applyRuleSum(smkey: string, group: any[]) {
	let sum = new Decimal(0);
	let filteredGroup = group.filter((groupItem) => {
		for (let key of Object.keys(groupItem)) {
			if (key.includes("year") && groupItem[key] === 1900) {
				return false;
			}
		}
		return true;
	});
	// console.log(filteredGroup);
	group.forEach((queryResult) => {
		sum = sum.add(new Decimal(queryResult[smkey]));
	});
	return Number(sum.toNumber().toFixed(2));
}

export function applyRuleMax(smKey: string, group: any[]) {
	let max = group[0][smKey];
	group.forEach((queryResult) => {
		if (queryResult[smKey] > max) {
			max = queryResult[smKey];
		}
	});
	return max;
}

export function applyRuleMin(smKey: string, group: any[]) {
	let min = group[0][smKey];
	group.forEach((queryResult) => {
		if (queryResult[smKey] < min) {
			min = queryResult[smKey];
		}
	});
	return min;
}

export function applyRuleCount(smKey: string, group: any[]) {
	let uniqueValues = new Set(
		group.map((item) => {
			return item[smKey];
		})
	);
	return uniqueValues.size;
}

export function applyRuleAvg(smKey: string, group: any[]) {
	let sum = new Decimal(0);
	let filteredGroup = group.filter((groupItem) => {
		return groupItem["year"] !== 1900;
	});
	filteredGroup.forEach((queryResult) => {
		sum = sum.add(new Decimal(queryResult[smKey]));
	});
	return Number((sum.toNumber() / filteredGroup.length).toFixed(2));
}

export function makeGroups(groupKeys: string[], queryResults: any[]) {
	const groups = new Map<string, any[]>();
	for (const result of queryResults) {
		const groupKey = groupKeys.map((key) => result[key]).join("|");
		const group = groups.get(groupKey);
		if (group) {
			group.push(result);
		} else {
			groups.set(groupKey, [result]);
		}
	}
	return Array.from(groups.values());
}

export function deepEquals(obj1: any, obj2: any) {
	let keys = Object.keys(obj1);
	for (let key of keys) {
		if (obj1[key] !== obj2[key]) {
			return false;
		}
	}
	return true;
}

export function getGroupKeys(query: any) {
	return query;
}

export function performWhere(query: any, section: object): boolean {
	let result: boolean = true;
	if (Object.keys(query).length === 0) {
		// For instance {"WHERE": {}};
		return true;
	}
	let key = Object.keys(query)[0];
	let value = query[key as keyof typeof query];
	switch (key) {
		case "AND":
			result = true;
			for (let item of value) {
				result = result && performWhere(item, section);
			}
			return result;
		case "OR":
			result = false;
			for (let item of value) {
				result = performWhere(item, section) || result;
			}
			return result;
		case "LT":
			return (result = lt(value, section));
		case "GT":
			return (result = gt(value, section));
		case "EQ":
			return (result = eq(value, section));
		case "IS":
			return (result = is(value, section));
		case "NOT":
			return (result = !performWhere(value, section));
		default:
			throw new InsightError("Invalid filter key: " + key);
	}
}

export function selectKeyValuesInColumn(queryContents: object[], columns: string[]) {
	for (let result of queryContents) {
		for (let key of Object.keys(result)) {
			if (!columns.includes(key)) {
				delete result[key as keyof typeof result];
			}
		}
	}
}

export function validateResultSize(queryContents: any) {
	if (queryContents.length > 5000) {
		throw new ResultTooLargeError("Too many results!");
	}
}

export function getAnyKeys(query: any): string[] {
	return query["OPTIONS"]["COLUMNS"];
}

export function lt(mKeyValuePair: object, section: object): boolean {
	for (const [key1, value1] of Object.entries(section)) {
		for (const [key2, value2] of Object.entries(mKeyValuePair)) {
			if (key1 === key2) {
				return value2 > value1;
			}
		}
	}
	return false;
}

export function gt(mKeyValuePair: object, section: object): boolean {
	for (const [key1, value1] of Object.entries(section)) {
		for (const [key2, value2] of Object.entries(mKeyValuePair)) {
			if (key1 === key2) {
				return value2 < value1;
			}
		}
	}
	return false;
}
export function eq(mKeyValuePair: object, section: object): boolean {
	for (const [key1, value1] of Object.entries(section)) {
		for (const [key2, value2] of Object.entries(mKeyValuePair)) {
			if (key1 === key2) {
				return value2 === value1;
			}
		}
	}
	return false;
}

export function is(mKeyValuePair: object, section: object): boolean {
	let result = true;
	for (const [queryKey, queryValue] of Object.entries(mKeyValuePair)) {
		for (const [sectionKey, sectionValue] of Object.entries(section)) {
			if (queryKey === sectionKey) {
				let string = queryValue;
				if (string === "*" || string === "**") {
					return true;
				} else if (string.startsWith("*") && string.endsWith("*")) {
					string = string.slice(1, -1);
					return sectionValue.includes(string);
				} else if (string.startsWith("*")) {
					string = string.slice(1);
					return sectionValue.endsWith(string);
				} else if (string.endsWith("*")) {
					let asteriskIndex = string.indexOf("*");
					string = string.slice(0, asteriskIndex);
					return sectionValue.startsWith(string);
				} else {
					return string === sectionValue;
				}
			}
		}
	}
	return false;
}

export async function getData() {
	let fileNames = await fs.readdir("./data");
	let filePromises = fileNames.map(async (fileName) => {
		try {
			let fileContent = await fs.readJson(`./data/${fileName}`);
			let jsonString = JSON.stringify(fileContent);
			let data = JSON.parse(jsonString);
			return data.data;
		} catch (err) {
			throw new InsightError("Corrupted persisted file");
		}
	});
	let array: object[] = [];
	try {
		let rawResults: any[] = await Promise.all(filePromises);
		let refinedResults: object[][] = rawResults.filter(Array.isArray);
		for (let result of refinedResults) {
			array = array.concat(result);
		}
		return array;
	} catch (err) {
		throw new InsightError("Blah");
	}
}

import {InsightDatasetKind, InsightError, InsightResult, ResultTooLargeError} from "./IInsightFacade";
import JSZip = require("jszip");
import * as fs from "fs-extra";
import {validateNoDuplicateId} from "./AddDatasetHelperFunctions1";
import {validateOption, validateColumns, validateOptionKeys, validateOrder} from "./ValidateQueryHelperFunctions2";

let validSKeys = [
	"uuid",
	"id",
	"title",
	"instructor",
	"dept",
	"fullname",
	"shortname",
	"number",
	"name",
	"address",
	"type",
	"furniture",
	"href",
];
let validMKeys = ["year", "avg", "pass", "fail", "audit", "", "lat", "lon", "seats"];
export function getQueryAsJson(query: unknown) {
	let queryContent;
	try {
		let jsonContent = JSON.stringify(query);
		queryContent = JSON.parse(jsonContent);
	} catch (err) {
		throw new InsightError("Invalid query");
	}
	return queryContent;
}

export async function validateQuery(query: any) {
	let queryKeys = Object.keys(query);
	validateQueryKeys(queryKeys); // Ensures that WHERE and OPTIONS exist
	let whereKeys: any[] = [];
	validateAsObject("WHERE", query.WHERE);
	validateWhere(query.WHERE, whereKeys);
	validateAsObject("OPTIONS", query.OPTIONS);
	await validateOption(query.OPTIONS);
	let columnKeys = query.OPTIONS["COLUMNS"];
	let totalKeys: string[] = columnKeys.concat(whereKeys);
	if (queryKeys.includes("TRANSFORMATIONS")) {
		validateAsObject("TRANSFORMATIONS", query.TRANSFORMATIONS);
		validateTranformation(query.TRANSFORMATIONS, columnKeys);

		let groupKeys = query.TRANSFORMATIONS["GROUP"];
		let applyKeys = getApplyKeys(query.TRANSFORMATIONS["APPLY"]);
		validateColumnsMustBeInGroupOrApply(columnKeys, groupKeys, applyKeys);

		totalKeys = totalKeys.concat(groupKeys);
	}
	validateAccessingOneDatasetOnly(totalKeys);
}

export function validateAccessingOneDatasetOnly(totalKeys: any[]) {
	totalKeys = totalKeys.filter((key) => {
		return key.includes("_");
	});
	totalKeys = totalKeys.map((key) => {
		return key.split("_")[0];
	});
	let set = new Set(totalKeys);
	if (set.size !== 1) {
		throw new InsightError("Can only access one dataset at time");
	}
}

export function validateColumnsMustBeInGroupOrApply(columnKeys: any[], groupKeys: any[], applyKeys: any[]) {
	for (let columnKey of columnKeys) {
		if (!groupKeys.includes(columnKey) && !applyKeys.includes(columnKey)) {
			throw new InsightError("Column keys must be in either a group key or apply key");
		}
	}
}

export function getApplyKeys(applyRules: any[]) {
	let applyKeys = [];
	for (let applyRule of applyRules) {
		let applyRuleKey = Object.keys(applyRule)[0];
		applyKeys.push(applyRuleKey);
	}
	return applyKeys;
}

export function validateTranformation(query: any, columns: any[]) {
	let transformationKeys = Object.keys(query);
	validateTransformationKeys(transformationKeys);
	validateGroup(query["GROUP"], columns);
	validateApply(query["APPLY"], columns);
}

export function validateGroup(groupKeys: any, columns: any[]) {
	validateAsArray("GROUP", groupKeys);
	if (groupKeys.length === 0) {
		throw new InsightError("GROUP must not be an empty array");
	}
	for (let groupKey of groupKeys) {
		let smKey = groupKey.split("_")[1];
		if (!validSKeys.includes(smKey) && !validMKeys.includes(smKey)) {
			throw new InsightError("GROUP key must be an smKey");
		}
		// if (!columns.includes(groupKey)) {
		// 	throw new InsightError("GROUP key must be in COLUMNS");
		// }
	}
}

export function validateApply(applyRuleList: any[], columns: any[]) {
	validateAsArray("APPLY", applyRuleList);
	for (let applyRule of applyRuleList) {
		validateArrayRule(applyRule, columns);
	}
}

export function validateArrayRule(arrayRule: any, columns: any[]) {
	validateAsObject("ARRAYRULE", arrayRule);
	let arrayRuleKeys = Object.keys(arrayRule);
	if (arrayRuleKeys.length !== 1) {
		throw new InsightError("ARRAYRULE must have only one key value pair");
	}
	let arrayRuleKey = arrayRuleKeys[0];
	if (arrayRuleKey.includes("_")) {
		throw new InsightError("ARRAYRULE key must not have an underscore");
	}
	if (!columns.includes(arrayRuleKey)) {
		throw new InsightError("ARRAYRULE applykey must be in COLUMNS");
	}
	validateApplyRuleValue(arrayRule[arrayRuleKey]);
}

export function validateApplyRuleValue(applyRuleValue: any) {
	validateAsObject("APPLYRULEVALUE", applyRuleValue);
	let applyRuleValueKeys = Object.keys(applyRuleValue);
	if (applyRuleValueKeys.length !== 1) {
		throw new InsightError("ARRAYRULEVALUE keys must have only one key value pair");
	}
	let validApplyTokens = ["MAX", "MIN", "AVG", "COUNT", "SUM"];
	let applyToken = applyRuleValueKeys[0];
	if (!validApplyTokens.includes(applyToken)) {
		throw new InsightError("APPLYTOKEN must be either 'MAX', 'MIN', 'AVG', 'COUNT', or 'SUM'");
	}
	let applyRuleValueValue = applyRuleValue[applyRuleValueKeys[0]];
	let smKey = applyRuleValueValue.split("_")[1];
	if (applyToken === "COUNT") {
		if (!validMKeys.includes(smKey) && !validSKeys.includes(smKey)) {
			throw new InsightError("APPLYRULEVALUEVALUE must be an mKey");
		}
	} else {
		if (!validMKeys.includes(smKey)) {
			throw new InsightError("APPLYRULEVALUEVALUE must be an mKey");
		}
	}
}

export function validateTransformationKeys(keys: any[]) {
	let validKeys = ["GROUP", "APPLY"];
	for (let key of keys) {
		if (!validKeys.includes(key)) {
			throw new InsightError("TRANSFORMATION main keys must be either 'GROUP' or 'APPLY'");
		}
	}
	if (!keys.includes("GROUP") && !keys.includes("APPLY")) {
		throw new InsightError("TRANSFORMATION main keys must include 'GROUP' and 'APPLY'");
	}
}

export function validateQueryKeys(queryKeys: string[]) {
	let validKeys = ["WHERE", "OPTIONS", "TRANSFORMATIONS"];
	for (let queryKey of queryKeys) {
		if (!validKeys.includes(queryKey)) {
			throw new InsightError("Invalid main query key(s)");
		}
	}
	if (!queryKeys.includes("WHERE") || !queryKeys.includes("OPTIONS")) {
		throw new InsightError("Missing WHERE or OPTIONS");
	}
}

export function validateWhere(query: object, whereKeys: any[]) {
	Object.entries(query).forEach(([key, value]) => {
		if (typeof value !== "object") {
			throw new InsightError("Query values must be an object or an array");
		}
		switch (key) {
			case "AND":
			case "OR":
				validateAsArray(key, value);
				validateANDOR(value);
				for (let item of value) {
					validateWhere(item, whereKeys);
				}
				break;
			case "LT":
			case "GT":
			case "EQ":
				validateAsObject(key, value);
				validComparison(value, "number", whereKeys);
				break;
			case "IS":
				validateAsObject(key, value);
				validComparison(value, "string", whereKeys);
				break;
			case "NOT":
				validateAsObject(key, value);
				validateWhere(value, whereKeys);
				break;
			default:
				throw new InsightError("This WHERE key must be string: " + key);
		}
	});
}

export function validateAsObject(key: string, value: any) {
	if (typeof value !== "object" || Array.isArray(value)) {
		throw new InsightError(`${key} values must be an object`);
	}
}

export function validateAsArray(key: string, value: any) {
	if (!Array.isArray(value)) {
		throw new InsightError(`${key} value must be an array`);
	}
}

export function validateANDOR(value: any) {
	if (value.length === 0) {
		throw new InsightError("AND OR Cannot be an empty array");
	}
}

export function validComparison(query: object, type: string, whereKeys: any[]) {
	validateKeys(Object.keys(query), type);
	if (type === "string") {
		validateSValue(Object.values(query));
	}
	if (type === "number") {
		validateMValue(Object.values(query), type);
	}
	whereKeys.push(Object.keys(query)[0]);
}

export function validateMValue(values: any[], type: string) {
	if (values.length !== 1) {
		throw new InsightError("Invalid number of keys");
	}
	let value = values[0];
	if (typeof value !== "number") {
		throw new InsightError("Wrong type!");
	}
}

export function validateSValue(values: any[]) {
	if (values.length !== 1) {
		throw new InsightError("Invalid number of values");
	}
	let value = values[0];
	if (typeof value !== "string") {
		throw new InsightError("Wrong type!");
	}
	if (value === "" || value === "**" || value === "*") {
		// These are special valid values.
	}
	let asteriskParts = value.split("*");
	if (asteriskParts.length > 3) {
		throw new InsightError("Too many asterisks");
	} else if (asteriskParts.length === 3) {
		if (asteriskParts[1] === "") {
			if (asteriskParts[0] !== "" || asteriskParts[2] !== "") {
				throw new InsightError("Asterisks can only be the first or last character");
			}
		} else {
			if (asteriskParts[0] !== "" || asteriskParts[2] !== "") {
				throw new InsightError("Asterisks can't be in between characters");
			}
		}
	} else if (asteriskParts.length === 2) {
		if (asteriskParts[0] !== "" && asteriskParts[1] !== "") {
			throw new InsightError("Asterisks can't be in the middle of the characters");
		}
	}
}

export function validateKeys(keys: string[], keyType: string): void {
	// GT, LT, EQ, and IS should have a key list size of 1.
	if (keys.length !== 1) {
		throw new InsightError("Invalid number of keys");
	}
	// GT, LT, EQ, and IS shouldn't have an empty key
	let key = keys[0];
	if (key === "") {
		throw new InsightError("Missing key");
	}
	let keyParts = key.split("_");
	if (keyParts.length !== 2) {
		throw new InsightError("Incorrect number of underscores in key");
	}
	if (keyParts[0] === "") {
		throw new InsightError("Key doesn't reference a dataset!");
	}
	let dataSet = keyParts[1];
	if (dataSet === "") {
		throw new InsightError("Missing smkey");
	}
	if (keyType === "number" && !validMKeys.includes(dataSet)) {
		throw new InsightError("Invalid mkey");
	}
	if (keyType === "string" && !validSKeys.includes(dataSet)) {
		throw new InsightError("Invalid skey");
	}
	if (keyType === "either" && !validSKeys.includes(dataSet) && !validMKeys.includes(dataSet)) {
		throw new InsightError("Invalid smkey");
	}
}

